import React, { Component } from 'react';
import Ninjas from './Ninjas';
import AddNinja from './AddNinja'

class App extends Component {
  state = {
    ninjas: [
      { name:'Lang', age:'30', belt:'blue', id:1},
      { name:'rang', age:'20', belt:'black',id:2},
      { name:'ozang', age:'40', belt:'blur',id:3}
    ]
  }
  addNewNinja = (ninja) => {
    ninja.id = Math.random
  }
  render(){
  return (
    <div className="App">
      <p>Welcome </p>
     <Ninjas ninjas={this.state.ninjas}/>
     <AddNinja aNewNinja={this.addNewNinja}/>

    </div>
  );
}}

export default App;
