            e.preventDefault();
            console.log(`form submitted by ${this.state.name}`)
        }


    render(){
        return <div>
        <h1>Welcome humanoids</h1>
        <p>{`${this.state.name} is a ${this.state.bio}`}</p>
        <form onSubmit={this.submitName}>
        <input type="text" onChange={this.changeName}/>
        <button>Submit</button>
        </form>


